import React from "react";



function Appp(){ 

    let currDate=new Date().getHours;
    let gretting ='';
    const ritul={};

    if(currDate>=1 && currDate< 12){
              
        gretting='Good Morning';
        ritul.color="green";
    }

    else if(currDate>=12 && currDate< 19){
        gretting='Good Afternoon';
        ritul.color="orange";
    }

    else{
        gretting='Good Evening';
        ritul.color="black";
    }

    return (  <>   <div className="janu"> <h1  style= {{  color:"gold"}}>Hii Ritul üñêüñê <span style={ritul}>{gretting}</span>  </h1></div>  </>) ;
}


